mapper.select:
select * 
from task_get
where sub_task_id = #{subTaskID} and status = #{status}

select * 
from task_get
where sub_task_id = #{subTaskID} and accepter = #{accepter}

select * 
from task_get
where accepter = #{accepter} and task_id = #{taskID}

mapper.update:
update task_get set
	status = #{status}
where task_id = #{taskID} and accepter = #{accepter}

update task_get set
	status = #{status}
where id = #{taskGetID}

update task_get set
	remark = #{remark}
where task_id = #{taskID} and accepter = #{accepter}

update task_get set
	remark = #{remark}
where id = #{taskGetID}
